tinymce.PluginManager.add('toggle_fullscreen', function(editor) {
	'use strict';
	editor.addButton('toggle_fullscreen', {
		title: 'Screen Toggle',
		icon: false,
		image: 'data:image/svg+xml;base64,',
		cmd: 'mceFullscreen'
	});
});

